// Helper functions to get unique cities
export const getAllUniqueCities = (): string[] => {
  const cities = Array.from(new Set(comprehensiveHospitalDatabase.map(h => h.city)));
  return cities.sort((a, b) => a.localeCompare(b)); // Alphabetically sorted
};

// Helper function to get hospitals by city
export const getHospitalsByCity = (city: string): ComprehensiveHospital[] => {
  return comprehensiveHospitalDatabase.filter(h => h.city.toLowerCase() === city.toLowerCase());
};

// Helper function to search cities by prefix
export const searchCitiesByPrefix = (prefix: string): string[] => {
  const normalizedPrefix = prefix.toLowerCase().trim();
  if (!normalizedPrefix) return getAllUniqueCities();
  
  const uniqueCities = getAllUniqueCities();
  return uniqueCities.filter(city => city.toLowerCase().startsWith(normalizedPrefix));
};

// Helper function to get hospitals within radius (simulated - in real app would use coordinates)
export const getHospitalsWithinRadius = (city: string, radiusKm: number = 100): ComprehensiveHospital[] => {
  // In a real application, this would use actual coordinates and calculate distances
  // For now, we'll return hospitals from the same city and nearby cities in the same state
  const selectedHospitals = getHospitalsByCity(city);
  
  if (selectedHospitals.length === 0) return [];
  
  const state = selectedHospitals[0].state;
  
  // Get all hospitals from the same state (simulating 100km radius)
  const nearbyHospitals = comprehensiveHospitalDatabase.filter(h => 
    h.state === state && h.city.toLowerCase() !== city.toLowerCase()
  );
  
  // Combine and return (in real app, would sort by actual distance)
  return [...selectedHospitals, ...nearbyHospitals.slice(0, 20)];
};

// Get all states
export const getAllStatesFromHospitals = (): string[] => {
  const states = Array.from(new Set(comprehensiveHospitalDatabase.map(h => h.state)));
  return states.sort();
};

// Get cities by state
export const getCitiesByState = (state: string): string[] => {
  const cities = Array.from(new Set(
    comprehensiveHospitalDatabase
      .filter(h => h.state === state)
      .map(h => h.city)
  ));
  return cities.sort();
};
